package eight;

public class AssignmentOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=20;
		int y=10;
		x+=y;//x=x+y=30
		System.out.println("x="+x);
		
        x-=y;//x=x-y=20
        System.out.println("x="+x);
		
        x*=y;//x=x*y=200
        System.out.println("x="+x);
		
        x/=y;//x=x/y=20
        System.out.println("x="+x);
        
        
        x%=y;//x=x%y=0
        System.out.println("x="+x);
		
	}

}
